struct swap_args {
    pid_t child_pid;
    ak_fiot fctx;
    int amaster;
};

static void *swap_thread(void *ptr)
{
    struct swap_args *args = (struct swap_args *)ptr;
    pid_t pid;
    ak_fiot fctx;
    int socket_fd, amaster, max_fd, ret;
    fd_set rset;
    char buffer[DATA_MAX_SIZE];

    pid = args->child_pid;
    fctx = args->fctx;
    amaster = args->amaster;
    free(args);
    size_t length;
    message_t mtype = undefined_message;
    ak_uint8 *data = NULL;
    frame_type_t ftype;

    max_fd = (amaster > fctx->iface_enc) ? amaster : fctx->iface_plain;

    while (1) {
        FD_ZERO(&rset);
        FD_SET(fctx->iface_enc, &rset);
        FD_SET(amaster, &rset);

        ret = select(max_fd + 1, &rset, NULL, NULL, &(struct timeval) { COMMAND_TIMEOUT, 0 });

        if (ret == -1) {
            debug_print("select() error\n");
            break;
        } else if (ret == 0) {
            debug_print("select() timeout\n");
            break;
        }

        if (FD_ISSET(fctx->iface_enc, &rset)) {
//            if ((ret = read(socket_fd, buffer, DATA_MAX_SIZE)) > 0) {
            if ((data = ak_fiot_context_read_frame( fctx, &length, &mtype, &ftype )) != NULL) {
                if (write(amaster, data, length) != length) {
                    debug_print("cannot write to pty\n");
                    break;
                }
            } else {
                debug_print("ak_fiot_context_read_frame() error\n");
                break;
            }
        }

        if (FD_ISSET(amaster, &rset)) {
            if ((ret = read(amaster, buffer, DATA_MAX_SIZE)) > 0) {
//                if (write(socket_fd, buffer, ret) != ret) {
                if (ak_fiot_context_write_frame(fctx, buffer, ret, encrypted_frame, application_data) != ak_error_ok) {
                    debug_print("ak_fiot_context_write_frame() error\n");
                    break;
                }
            } else {
                debug_print("cannot read from pty\n");
                break;
            }
        }
    }

    debug_print("connection has lost\n");

    kill(pid, SIGKILL);
    close(amaster);
//    close(socket_fd);
    ak_fiot_context_destroy( fctx );
    pthread_exit(NULL);
}

int spawn_pty_client(ak_fiot fctx)
{
    int pid = -1, amaster = -1, aslave = -1;
    int res = EXIT_FAILURE;
    pthread_t tid;
    struct winsize winsize;
    struct swap_args *swap_args = malloc(sizeof(struct swap_args));
    size_t length;
    message_t mtype = undefined_message;
    frame_type_t ftype;

    ak_uint8 *data = ak_fiot_context_read_frame( fctx, &length, &mtype, &ftype );
    if(length != sizeof(struct winsize)) {
        debug_print("error receiving winsize from host\n");
        goto cleanup;
    }
    else {
        memcpy(&winsize, data, length);
    }

//    if (read(socket_fd, (char *) &winsize, sizeof(struct winsize)) != sizeof(struct winsize)) {
//        debug_print("error receiving winsize from host\n");
//        goto cleanup;
//    }
    debug_print("got window size --> %d:%d\n", winsize.ws_col, winsize.ws_row);

    if (openpty(&amaster, &aslave, NULL, NULL, NULL) < 0) {
        debug_print("openpty() error: %s\n", strerror(errno));
        goto cleanup;
    }

    if (ioctl(amaster, TIOCSWINSZ, &winsize) < 0) {
        debug_print("ioctl() error: %s\n", strerror(errno));
        goto cleanup;
    }

    if ((pid = fork()) < 0) {
        debug_print("fork() error: %s\n", strerror(errno));
        goto cleanup;
    }

    if (pid == 0) {
        /* child */

//        ak_fiot_context_destroy( fctx );
//        close(socket_fd);
        close(amaster);

        if (setsid() < 0) {
            debug_print("setsid(): %s\n", strerror(errno));
        }

//        setuid(1000);

        if (ioctl(aslave, TIOCSCTTY, NULL) < 0) {
            debug_print("ioctl() error: %s\n", strerror(errno));
        }

        dup2(aslave, STDIN_FILENO);
        dup2(aslave, STDOUT_FILENO);
        dup2(aslave, STDERR_FILENO);

        if (aslave > 2) {
            close(aslave);
        }

        char bash[100] = "/bin/bash";
        execl(bash, bash, NULL);


        /* if there is no bash */
        char sh[100] = "/bin/sh";
        execl(sh, sh, NULL);

        /* never executed */
        exit(0);
    }

    /* parent */

    close(aslave);

    swap_args->child_pid = pid;
    swap_args->fctx = fctx;
    swap_args->amaster = amaster;

    swap_thread(swap_args);

    res = EXIT_SUCCESS;
cleanup:
    if (res) {
        if (pid > 0)
            kill(pid, SIGKILL);
        if (amaster != -1)
            close(amaster);
//        close(socket_fd);
        ak_fiot_context_destroy( fctx );
    }
    return res;
}

 int main( int argc, char *argv[] )
{
  char ip[16];
  struct fiot ctx;
  socklen_t opt = 0;
  struct sockaddr_in servaddr, cl_addr;
  socklen_t len = sizeof( struct sockaddr_in );
  int error = ak_error_ok, fd = -1, reuse = 1, done = 0;
  ak_socket listenfd = ak_network_undefined_socket;

 /* проверяем, что определен ip адрес сервера и порт */
  if( argc != 3 ) {
    printf("usage: echo-server ip_address port\n");
    return EXIT_SUCCESS;
  }

  /* часть первая: создание сокетов */

  /* инициализируем библиотеку на стороне сервера
     вывод сообщений аудита производится в стандартный поток ошибок */
   if( !ak_libakrypt_create( ak_function_log_stderr )) return ak_libakrypt_destroy();
  /* устанавливаем максимальный уровень аудита */
//   ak_log_set_level( fiot_log_maximum );

  /* создаем сокет */
   if(( listenfd = ak_network_socket( AF_INET, SOCK_DGRAM, 0 )) == ak_network_undefined_socket )
     return ak_error_message_fmt( -1,  __func__,
                                          "wrong creation of listening socket (%s)", strerror(errno));
   memset( &servaddr, 0, sizeof( struct sockaddr_in ));
   servaddr.sin_family = AF_INET;
   if( ak_network_inet_pton( AF_INET, argv[1], &servaddr.sin_addr.s_addr ) != ak_error_ok )
     return ak_error_message_fmt( -1, __func__, "incorrect assigning server ip %s address (%s)",
                                                                          argv[1], strerror( errno ));
   servaddr.sin_port = htons( atoi( argv[2] ));

  /* разрешаем запускать bind() на используемом адресе */
   ak_network_setsockopt( listenfd, SOL_SOCKET, SO_REUSEADDR, &reuse, sizeof ( reuse ));
   if( ak_network_bind( listenfd,  &servaddr, sizeof( servaddr )) != ak_error_ok )
     return ak_error_message_fmt( -1, __func__,
                                          "wrong binding of listening socket (%s)", strerror(errno));

  /* принимаем соединения */
   opt = sizeof( cl_addr );

   char msg;
   if (ak_network_recvfrom(listenfd, &msg, 1, MSG_PEEK, &cl_addr, &opt) <= 0) {
                ak_network_close(listenfd);
                return ak_error_message(ak_error_read_data, __func__, "wrong first client message receiving");
   }
   
   fd = listenfd;
   
   if( ak_network_inet_ntop( AF_INET, &cl_addr.sin_addr, ip, (socklen_t) sizeof( ip )) == NULL )
     return ak_error_message_fmt( -1, __func__,
                                        "can't determine client's address (%s)", strerror( errno ));
   printf( "echo-server: accepted client from %s:%u\n", ip, cl_addr.sin_port );


  /* часть вторая: аутентификация клиента и выполнение протокола выработки общих ключей */


  /* устанавливаем криптографические параметры взаимодействия и запускаем протокол выработки ключей */
  /* создаем контекст защищенного соединения */
   if(( error = ak_fiot_context_create( &ctx )) != ak_error_ok )
     return ak_error_message( error, __func__, "incorrect fiot context creation" );

  /* устанавливаем роль */
   if(( error = ak_fiot_context_set_role( &ctx, server_role )) != ak_error_ok ) goto exit;
  /* устанавливаем идентификатор сервера */
   if(( error = ak_fiot_context_set_user_identifier( &ctx, server_role,
                                                       "serverID", 8 )) != ak_error_ok ) goto exit;
  /* устанавливаем сокет для внешнего (шифрующего) интерфейса */
   if(( error = ak_fiot_context_set_interface_descriptor( &ctx,
                                            encryption_interface, fd )) != ak_error_ok ) goto exit;
   if(( error = ak_fiot_context_set_blom_key( &ctx, blom_key )) != ak_error_ok ) goto exit;
   if(( error = ak_fiot_context_set_client( &ctx,
                                            cl_addr )) != ak_error_ok ) goto exit;
  /* устанавливаем набор криптографических алгоритмов для обмена зашифрованной информацией */
   if(( error =  ak_fiot_context_set_server_policy( &ctx,
                                            magmaCTRplusGOST3413 )) != ak_error_ok ) goto exit;
  /* теперь выполняем протокол */
   if(( error = ak_fiot_context_keys_generation_protocol( &ctx )) != ak_error_ok ) goto exit;
   printf( "echo-server: client authentication is Ok\n" );


  /* часть третья: получение и возврат сообщений */

  spawn_pty_client(&ctx);

  exit:
   ak_fiot_context_destroy( &ctx );
   ak_network_close( listenfd );
   ak_libakrypt_destroy();

 return EXIT_SUCCESS;
}
